import spacy
import pandas as pd
import tqdm
from spacy import displacy
import time

def NER(text):

    # Loading the English model
    nlp = spacy.load('en_core_web_sm')
    text = text
    # text = "During a six-month period, paramedics acquired prehospital 12-lead ECGs on adult chest pain patients. Investigators interpreted ECGs independently, retrospectively, and blinded to patient outcome. ECGs were classified as meeting or not meeting the six ST segment elevation criteria regardless of ECG morphology if the criteria were present in two or more anatomically contiguous leads: 1 mm or more ST segment elevation; 2 mm or more ST segment elevation; 1 mm or more ST segment elevation in the limb leads or 2 mm or more ST segment elevation in the precordial leads; and the first three criteria with the simultaneous presence of reciprocal changes. ECGs that did not meet any ST segment elevation criteria were classified as normal, nonspecific ST/T wave changes, abnormal but not ischemic, and ischemic. Hospital charts were reviewed for final cardiac diagnosis."

    # Processing the text
    doc = nlp(text)

    for entity in doc.ents:
        print(entity.text, entity.label_)

    displacy.serve(doc, style="dep", auto_select_port=True)

    entities = pd.read_csv("entities.tsv", delimiter="\\t")
    print(entities.head())
    list_off0 = list(entities['offset1'])
    list_off1 = list(entities['offset2'])

    text_files_path = "data/text"

    f = open(text_files_path + "/" + entities.iloc[1,0] + ".txt", "r", encoding="UTF-8")
    for l in f:
      print(l)

    # l = text
    # print(l)

    #Clinical cases
    HCs = {}
    for fid in tqdm.tqdm(range(len(entities["filename"]))):
      fname = entities["filename"][fid]
      with open(text_files_path + "/" + fname + ".txt", "r", encoding="UTF-8") as f:
        HCs.update({fname: f.read()})


    #Diseases
    ENF = {}
    enfermedades = []
    fn = entities["filename"][0]
    for fname, enf in zip(entities["filename"], entities["span"]):
        if fname!=fn:
          enfermedades = []
        enfermedades.append(enf)
        ENF.update({fname: enfermedades})
        fn = fname

    print("oi")

    tokens = ""

    HCs_tokenized = []
    for hc in HCs:
        hl = []
        tokens = nlp(HCs[hc])
        #tokens = HCs[hc].split(" ") #The simplest option
        for t in tokens:
            hl.append(str(t))
        HCs_tokenized.append(hl)

    print("oi1")


    Ent_tokenized = []
    for enf in ENF:
        Tks = []
        for e in ENF[enf]:
          sl = []
          tokens = nlp(e)
          #tokens = e.split(" ")
          for t in tokens:
              sl.append(str(t))
          Tks.append(sl)
        Ent_tokenized.append(Tks)

    print("oi2")


    def find_idx(list_to_check, item_to_find):
        indices = []
        for idx, value in enumerate(list_to_check):
            if value == item_to_find:
                indices.append(idx)
        return indices

    print("oi3")


    labels_tokenized = []
    idx = -1
    for hct, et in zip(HCs_tokenized, Ent_tokenized):
        idx += 1
        labels = []
        for i in range(len(hct)):
            # Labels: 0->'O'; 1->'B'; 2->'I'
            # labels.append('O')
            labels.append(0)

        # For Entities (Diseases|Enfermedades)
        for enf in et:
            first = True
            for e in enf:
                if first == True:
                    try:
                        # labels[hct.index(e)] = 'B'
                        # labels[posLab] = 'B'
                        indices = find_idx(hct, e)
                        if len(indices) > 1:
                            for id in indices:
                                labels[id] = 1
                        else:
                            labels[hct.index(e)] = 1

                        first = False
                    except:
                        first = False
                        if e == "sarcoma+carcinoma" or e == "carcinoma+sarcoma":
                            continue
                        print(hct)
                        print(et)
                        print(enf)
                        print(e)
                        print(idx)
                else:
                    try:
                        # labels[hct.index(e)] = 'I'
                        # labels[posLab] = 'I'
                        indices = find_idx(hct, e)
                        if len(indices) > 1:
                            for id in indices:
                                if labels[id - 1] != 0:
                                    labels[id] = 2
                        else:
                            labels[hct.index(e)] = 2
                    except:
                        if e == "sarcoma+carcinoma" or e == "carcinoma+sarcoma":
                            continue
                        print(hct)
                        print(et)
                        print(enf)
                        print(e)
                        print(idx)

        labels_tokenized.append(labels)

    print("oi4")


    j = 0
    for i in range(len(HCs_tokenized[j])):
        print(str(HCs_tokenized[j][i]) + "\t" + str(labels_tokenized[j][i]))


    flag = 0
    time.sleep(100000)
    for st, lt in zip(HCs_tokenized, labels_tokenized):
        if len(st) != len(lt):
            print(st)
            print(lt)
            flag = 1
    if flag==0:
        print("Everything is aligned!")


    sent_tokenized = []
    label_sent_tokenized = []
    for ht, lht in zip(HCs_tokenized, labels_tokenized):
      st = []; lbst = []
      for h, l in zip(ht,lht):
        if h != ".":
          st.append(h)
          lbst.append(l)
        else:
          st.append(".")
          lbst.append(0)
          sent_tokenized.append(st)
          label_sent_tokenized.append(lbst)
          st = []; lbst = []

    for i in range(len(label_sent_tokenized[0])):
        if label_sent_tokenized[0][i] != 0:
            print(label_sent_tokenized[0][i])
            print(sent_tokenized[0][i])

    print(label_sent_tokenized)
    displacy.serve(label_sent_tokenized, style="dep", auto_select_port=True)


